import React, { useEffect, useState } from 'react';
import { useInterval } from '../Lib/utils';

import './Board.css';
const BOARD_SIZE = 10;

const Board = () => {
    const [score, setScore] = useState(0);
    const [board, setBoard] = useState(createBoard(BOARD_SIZE));
    const [currentCell, setCurrentCell] = useState(new Set([]));
    const [openCells, setOpenCells] = useState(new Set([1]));

    useEffect(() => {
        window.addEventListener('onClick', e => {
            console.log("JUST CLICKED ONCE!!")
            handleClick(e);
        });
    }, []);

    useInterval(() => {
        openCell();
    }, 150);


    const handleClick = e => {
        console.log("CLICKED");
        openCells.add([30])
    };
    const openCell = () => {
        console.log("open cell")
        openCells.add([30])
    }


    const revealCell = (event, keyRow, keyCell) => {

        openCells.add(currentCell[0]);
        setOpenCells(openCells);

        setCurrentCell(new Set([keyRow * BOARD_SIZE + keyCell + 1]))
    }

    return (
        <>
            <h1>Score: {score}</h1>
            <div className="board">
                {board.map((row, rowIdx) => (
                    <div key={rowIdx} className="row">
                        {row.map((cellValue, cellIdx) => {
                            const className = getCellClassName(
                                cellValue,
                                currentCell,
                                openCells
                            );
                            return <div key={cellIdx} className={className} onClick={event => revealCell(event, rowIdx, cellIdx)}></div>;
                        })}
                    </div>
                ))}
            </div>
        </>
    );
};

//Auxiliary consts
const createBoard = BOARD_SIZE => {
    let counter = 1;
    const board = [];
    for (let row = 0; row < BOARD_SIZE; row++) {
        const currentRow = [];
        for (let col = 0; col < BOARD_SIZE; col++) {
            currentRow.push(counter++);
        }
        board.push(currentRow);
    }
    return board;
};

const getCellClassName = (
    cellValue,
    currentCell,
    openCells
) => {
    let className = 'cell cell-red';

    if (currentCell.has(cellValue)) className = 'cell cell-green';
    if (openCells.has(cellValue)) className = 'cell cell-purple';
    return className;
};
export default Board;
