    const [board, setBoard] = useState(createBoard(BOARD_SIZE));
    const [currentCell, setCurrentCell] = useState([-1, -1]);
    const [openCells, setOpenCells] = useState(new Set([]));
    const [displayPlusScore, setDisplayPlusScore] = useState(0);
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
    const [starStyle, setStarStyle] = useState('position:absolute;left150px;top:220px');

    const timeout = setTimeout(() => {
        setDisplayPlusScore(0);
    }, 4000);


    useEffect(() => {
        window.addEventListener('onClick', e => {
            handleClick(e);
        });
        return () => clearTimeout(timeout);
    }, []);

    useInterval(() => {
        openCell();
    }, 150);


    const handleClick = e => {
    };
    const openCell = () => {
    }


    const revealCell = (event, cellValue) => {
        setMousePosition({ x: event.clientX, y: event.clientY });
        if (currentCell[1] === cellValue[1]) {
            openCells.add(currentCell);
            openCells.add(cellValue);
            setOpenCells(openCells);
            setScore(score + reveals * 10);
            setReveals(reveals + 1);
            setCurrentCell([-1, -1]);
            setDisplayPlusScore(1);
        } else {
            if (currentCell[0] !== -1) {
                setReveals(1);
            }
            setCurrentCell(cellValue);
            setDisplayPlusScore(0);
        }
    }

    return (
        <>
            <h1>Score: {score}</h1>
            <div className="board">
                {board.map((row, rowIdx) => (
                    <div key={rowIdx} className="row">
                        {row.map((cellValue, cellIdx) => {
                            const className = getCellClassName(
                                cellValue,
                                currentCell,
                                openCells
                            );
                            const displayC = openCells.has(cellValue) || currentCell === cellValue ? cellValue[1] : '.';
                            return <div key={cellIdx} className={className} onClick={event => revealCell(event, cellValue)}>{displayC}</div>
                        })}
                    </div>
                ))}

                <div style={style}>{displayPlusScore === 0 ? mousePosition.x + ' ' + mousePosition.y : mousePosition.y + ' ' + mousePosition.x}</div>
            </div>
        </>
    );
};

//Auxiliary consts
const createBoard = BOARD_SIZE => {
    let counter = 1;
    const board = [];
    for (let row = 0; row < BOARD_SIZE; row++) {
        const currentRow = [];
        for (let col = 0; col < BOARD_SIZE; col++) {
            currentRow.push([counter++, row]);
        }
        board.push(currentRow);
    }
    return board;
};

const getCellClassName = (
    cellValue,
    currentCell,
    openCells
) => {
    let className = 'cell cell-red';

    if (currentCell == cellValue) className = 'cell-revealed cell-green';
    if (openCells.has(cellValue)) className = 'cell-revealed cell-purple';
    return className;
};
export default Board;
